using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AdventOfCode2016
{
  class Day2
  {
    static void Main2(string[] args)
    {
      const string instructions = @"



";

      const string inputKeypad = @"
  1  
 234 
56789
 ABC 
  D  ";

      char[][] keypad = inputKeypad.Split(new [] { Environment.NewLine }, StringSplitOptions.None).Skip(1).Select(line => line.ToArray()).ToArray();

      int x = 0;
      int y = 2;
      string code = "";

      foreach (var line in instructions.Split(new[] { Environment.NewLine }, StringSplitOptions.None))
      {
        foreach (var move in line)
        {
          int newX = x;
          int newY = y;

          switch (move)
          {
            case 'U':
              if (y>0) newY--;
              break;

            case 'D':
              if (y<keypad.Length-1) newY++;
              break;

            case 'L':
              if (x>0) newX--;
              break;

            case 'R':
              if (x<keypad[y].Length-1) newX++;
              break;
          }

          if (keypad[newY][newX] != ' ')
          {
            x = newX;
            y = newY;
          }
        }

        code += keypad[y][x];
      }

      Console.WriteLine(code);
      Console.ReadKey();
    }
  }
}
