using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AdventOfCode2016
{
  class Day2
  {
    static void Main(string[] args)
    {
      const string input = @"



";

      int[][] keypad =
      {
        new[] {1, 2, 3},
        new[] {4, 5, 6},
        new[] {7, 8, 9}
      };

      int x = 1;
      int y = 1;
      string code = "";

      foreach (var line in input.Split('\n'))
      {
        foreach (var move in line)
        {
          switch (move)
          {
            case 'U':
              if (y>0) y--;
              break;

            case 'D':
              if (y<2) y++;
              break;

            case 'L':
              if (x>0) x--;
              break;

            case 'R':
              if (x<2) x++;
              break;
          }
        }

        code += keypad[y][x].ToString();
      }

      Console.WriteLine(code);
      Console.ReadKey();
    }
  }
}
